import React, { Component } from "react";
import axios from 'axios';
import "./style.css";

class Finish extends Component {
  state={
    name:''
  }
  constructor(props) {
    super(props);

  }
  changeHandler=(e)=>{
    this.setState({[e.target.name]:e.target.value})
  }
submitHandler=(e)=>{
  e.preventDefault();
  const url = 'http://localhost:3000/score'
 // console.log(this.state);
  axios.post(url,this.state).then(response=>console.log(response))

}

  render() {
    const {name} = this.state;
    return (
      <div className="finish">
        <input  type="text" value={name} onChange={this.changeHandler} name="name"/>
        <button onClick={this.submitHandler}>Play again</button>
      </div>
    );
  }
}

export default Finish;
